import { mapGetters } from 'vuex'

export default {
  name: 'Student',
  computed: {
    ...mapGetters([
      'name'
    ])
  },
  data() {
    return {
      schools: [],
      academys: [],
      classs: [],
      options: [
      ],
      apiModel: 'student',
      form: {}
    }
  },

  mounted() {
    if (this.$route.query._id) {
      this.$http.post('/api/' + this.apiModel + '/get', { _id: this.$route.query._id }).then(res => {
        if (res && res.length > 0) {
          this.form = res[0]
          this.schoolChange(this.form.school)
        }
      })
    }

    // 显示学校选择栏目
    this.$http.post('/api/school/find').then(res => {
      if (res && res.length > 0) {
        this.schools = res
        console.log('res:', res)
      }
    })
    // 显示班级栏目
    this.$http.post('/api/classs/find').then(res => {
      if (res && res.length > 0) {
        this.classs = res
        console.log('res:', res)
      }
    })
  },
  methods: {
    onSubmit() {
      if (this.form._id) {
        this.$http.post(`/api/${this.apiModel}/update`, this.form).then(res => {
          console.log('bar:', res)
          this.$router.push({ path: this.apiModel })
          this.form = {}
        })
      } else {
        this.$http.post('/api/' + this.apiModel + '/add', this.form).then(res => {
          console.log('bar:', res)
          this.$router.push({ path: this.apiModel })
          this.form = {}
        })
      }
    },
    schoolChange(val1) {
      // 显示学院选择栏目
      this.$http.post('/api/academy/get', { school: val1 }).then(res => {
        if (res && res.length > 0) {
          this.academys = res
          console.log('res:', res)
        }
      })
    }
  }
}
